/* Stores my bathroom code */
var code = [];
/* Keypad */
var keypad = [
	[1,2,3],
	[4,5,6],
	[7,8,9]
];
/* Start on 5 */
var keypadIndex = [1,1];

/* Trying to figure it out */
input.forEach(function(item) {

	var directions = item.split("");

	directions.forEach(function(direction) {
		switch(direction) {
			case "U":
				keypadIndex[0] = keypadIndex[0] - 1;
			break;
			case "D":
				keypadIndex[0] = keypadIndex[0] + 1;
			break;
			case "R":
				keypadIndex[1] = keypadIndex[1] + 1;
			break;
			case "L":
				keypadIndex[1] = keypadIndex[1] - 1;
			break;
		};

		/* Ignore bad directions */
		if (keypadIndex[0] > 2) keypadIndex[0] = 2;
		if (keypadIndex[0] < 0) keypadIndex[0] = 0;
		if (keypadIndex[1] > 2) keypadIndex[1] = 2;
		if (keypadIndex[1] < 0) keypadIndex[1] = 0;
	});
	console.log(keypadIndex);
	code.push(keypad[keypadIndex[0]][keypadIndex[1]]);

});

console.log(code.join(""));





